mapper.select:
SELECT hid, huid, hoid, type, filepath, uptime, filesize,score
FROM history
WHERE huid = #{huid}

SELECT hid, huid, hoid, type, filepath, uptime, filesize,score
FROM history
WHERE hid = #{hid}

SELECT hid, huid, hoid, type, filepath, uptime, filesize,score
FROM history
WHERE huid = #{huid}
  and hoid = #{hoid}

mapper.update:
UPDATE history
SET score = #{score}
WHERE hid =
      #{hid}

